import * as React from "react";

import { LayoutContext } from "../../components/plasmic/market_v_2/PlasmicGlobalVariant__Layout";
import { PlasmicInvoicesetup } from "../../components/plasmic/remote_v_1/PlasmicInvoicesetup";
import { useRouter } from "next/router";

function Invoicesetup() {
  const router = useRouter();
  const [selectedNav, setSelectedNav] = React.useState("");

  const selectedStyles = {
    borderColor: "purple",
  };

  return (
    <LayoutContext.Provider value={undefined}>
      <PlasmicInvoicesetup
        switchLinkGroup={{
          children: null,
        }}
        entityNav={{
          style: selectedNav === "new" ? selectedStyles : {},
          onClick: () => {
            setSelectedNav("new");
          },
        }}
        individualNav={{
          style: selectedNav === "existing" ? selectedStyles : {},
          onClick: () => {
            setSelectedNav("existing");
          },
        }}
        formTrigger={{
          onClick: () => {
            if (!selectedNav) return;

            router.push(`/invoice/${selectedNav}/1`);
          },
        }}
      />
    </LayoutContext.Provider>
  );
}

export default Invoicesetup;
