import React, { useState, useEffect } from "react";
import { PlasmicMarket } from "../components/plasmic/market_v_2/PlasmicMarket";
import supabase from "../backend/api/supabase";
import { useRouter } from "next/router";
import Link from "next/link";

function Market() {
  const [listings, setListings] = useState([]);

  useEffect(() => {
    async function fetchAndSetListings() {
      let { data, error } = await supabase.from("Listing").select("*");
      if (!error) {
        setListings(data);
      }
    }
    fetchAndSetListings();
  }, []);

  const listingComponent = listings.map((listing) => {
    const linkHref = `/service-detail?id=${listing.id}`;
    return (
      <li key={listing.id}>
        <Link href={linkHref}>
        <a>{listing.name}</a>
        </Link>
      </li>
    );
  });

  return (
    <div>
      {listingComponent}
      <PlasmicMarket />;
    </div>
  );
}

export default Market;
